import Vue from "vue";
import store from "./store";
// import {isMobile} from "mobile-device-detect";
import Router from "vue-router";
import NProgress from "nprogress";
import authenticate from "./auth/authenticate";

Vue.use(Router);

// create new router

const routes = [
  {
    path: "/",
    component: () => import("./views/app"), //webpackChunkName app
    beforeEnter: authenticate,
    redirect: "/app/fpo",

    children: [
      {
        path: "/app/dashboards",
        component: () => import("./views/app/dashboards"), //dashboard
        children: [
          {
            path: "dashboard.v1",
            component: () => import("./views/app/dashboards/dashboard.v1")
          },
          {
            path: "dashboard.v2",
            component: () => import("./views/app/dashboards/dashboard.v2")
          },
          {
            path: "dashboard.v3",
            component: () => import("./views/app/dashboards/dashboard.v3")
          },
          {
            path: "dashboard.v4",
            component: () => import("./views/app/dashboards/dashboard.v4")
          }
        ]
      },


      {
        path: "/app/fpo",
        component: () => import("./views/app/pages"),
        redirect: "/app/fpo/ics",
        children: [
          {
            path: "all-ics",
            component: () => import("./views/app/oodo/fpo/ics")
          },
          {
            path: "ics",
            component: () => import("./views/app/oodo/fpo/ics")
          },
          {
            path: "ics/:action/:id?",
            component: () => import("./views/app/oodo/fpo/ics-form")
          },
          {
            path: "vfc",
            component: () => import("./views/app/oodo/fpo/vfc")
          },
          {
            path: "vfc/:action/:id?",
            component: () => import("./views/app/oodo/fpo/vfc-form")
          },
          {
            path: "farmers",
            component: () => import("./views/app/oodo/fpo/farmers")
          },
          {
            path: "farmer/:action/:id?",
            component: () => import("./views/app/oodo/fpo/farmer-form")
          },
          {
            path: "farmer-lands/:farmer_id?",
            component: () => import("./views/app/oodo/fpo/farmer/lands")
          },
          {
            path: "farmer-land/:action/:farmer_id/:id?",
            component: () => import("./views/app/oodo/fpo/farmer/land-form")
          },
          {
            path: "farmer-plant/:farmer_id?",
            component: () => import("./views/app/oodo/fpo/farmer/plants")
          },
          {
            path: "farmer-plant/:action/:farmer_id/:id?",
            component: () => import("./views/app/oodo/fpo/farmer/plant-form")
          },
          {
            path: "farmer-crops/:farmer_id?",
            component: () => import("./views/app/oodo/fpo/farmer/crops")
          },
          {
            path: "farmer-crops/:action/:farmer_id/:id?",
            component: () => import("./views/app/oodo/fpo/farmer/crop-form")
          },
          {
            path: "farmer-ics-payments/:farmer_id?",
            component: () => import("./views/app/oodo/fpo/farmer/ics-payments")
          },
          {
            path: "farmer-ics-payments/:action/:farmer_id/:id?",
            component: () => import("./views/app/oodo/fpo/farmer/ics-payment-form")
          },
          
        ]
      },

      {
        path: "/app/locations",
        component: () => import("./views/app/pages"),
        redirect: "/app/locations/countries",
        children: [
          {
            path: "countries",
            component: () => import("./views/app/oodo/locations/countries")
          },
          {
            path: "state",
            component: () => import("./views/app/oodo/locations/state")
          },
          {
            path: "state/:action/:id?",
            component: () => import("./views/app/oodo/locations/state-form")
          },
          {
            path: "district",
            component: () => import("./views/app/oodo/locations/district")
          },
          {
            path: "district/:action/:id?",
            component: () => import("./views/app/oodo/locations/district-form")
          },
          {
            path: "panchayath",
            component: () => import("./views/app/oodo/locations/panchayath")
          },
          {
            path: "panchayath/:action/:id?",
            component: () => import("./views/app/oodo/locations/panchayath-form")
          },
          {
            path: "revenue-village",
            component: () => import("./views/app/oodo/locations/revenue_village")
          },
          {
            path: "revenue-village/:action/:id?",
            component: () => import("./views/app/oodo/locations/revenue_village-form")
          },
          {
            path: "local-village",
            component: () => import("./views/app/oodo/locations/local_village")
          },
          {
            path: "local-village/:action/:id?",
            component: () => import("./views/app/oodo/locations/local_village-form")
          },
          {
            path: "post-office",
            component: () => import("./views/app/oodo/locations/post_office")
          },
          {
            path: "post-office/:action/:id?",
            component: () => import("./views/app/oodo/locations/post_office-form")
          },
          
        ]
      },


      {
        path: "/app/employee",
        component: () => import("./views/app/pages"),
        redirect: "/app/employee/employees",
        children: [
          {
            path: "employees",
            component: () => import("./views/app/oodo/employee/employees")
          },
          {
            path: "employee/:action/:id?",
            component: () => import("./views/app/oodo/employee/employee-form")
          },
          {
            path: "job-positions",
            component: () => import("./views/app/oodo/employee/job_position")
          },
          {
            path: "job-position/:action/:id?",
            component: () => import("./views/app/oodo/employee/job_position-form")
          },
          {
            path: "departments",
            component: () => import("./views/app/oodo/employee/departments")
          },
          {
            path: "department/:action/:id?",
            component: () => import("./views/app/oodo/employee/department-form")
          },
          {
            path: "aeo",
            component: () => import("./views/app/oodo/employee/aeo")
          },
          {
            path: "aeo/:action/:id?",
            component: () => import("./views/app/oodo/employee/aeo-form")
          },
          
        ]
      },

      {
        path: "/app/product",
        component: () => import("./views/app/pages"),
        redirect: "/app/product/product-price",
        children: [
          {
            path: "product-price",
            component: () => import("./views/app/oodo/product/product-price")
          },
          {
            path: "product-price/:action/:id?",
            component: () => import("./views/app/oodo/product/product-price-form")
          },
          
        ]
      },

      {
        path: "/app/procurement",
        component: () => import("./views/app/pages"),
        redirect: "/app/procurement/dashboard",
        children: [
          {
            path: "dashboard",
            component: () => import("./views/app/oodo/procurement/dashboard")
          },
          {
            path: "sample-procurement",
            component: () => import("./views/app/oodo/procurement/sample-procurement")
          },
          {
            path: "sample-procurement/:action/:id?",
            component: () => import("./views/app/oodo/procurement/sample-procurement-form")
          },
          {
            path: "normal-procurement",
            component: () => import("./views/app/oodo/procurement/normal-procurement")
          },
          {
            path: "normal-procurement/:action/:id?",
            component: () => import("./views/app/oodo/procurement/normal-procurement-form")
          },
          
        ]
      },

      {
        path: "/app/qc",
        component: () => import("./views/app/pages"),
        redirect: "/app/qc/dashboard",
        children: [
          {
            path: "dashboard",
            component: () => import("./views/app/oodo/qc/dashboard")
          },
          {
            path: "sample-materials-test",
            component: () => import("./views/app/oodo/qc/sample-materials-test")
          },
          {
            path: "sample-materials-test/:action/:id?",
            component: () => import("./views/app/oodo/qc/sample-materials-test-form")
          },
          {
            path: "normal-materials-test",
            component: () => import("./views/app/oodo/qc/normal-materials-test")
          },
          {
            path: "normal-materials-test/:action/:id?",
            component: () => import("./views/app/oodo/qc/normal-materials-test-form")
          },
          {
            path: "qc",
            component: () => import("./views/app/oodo/qc/qc")
          },
          {
            path: "qc/:action/:id?",
            component: () => import("./views/app/oodo/qc/qc-form")
          },
          {
            path: "lot-creation",
            component: () => import("./views/app/oodo/qc/lot-creation")
          },
          {
            path: "lot-creation/:action/:id?",
            component: () => import("./views/app/oodo/qc/lot-creation-form")
          },
          
        ]
      },

      {
        path: "/app/production",
        component: () => import("./views/app/pages"),
        redirect: "/app/production/dashboard",
        children: [
          {
            path: "dashboard",
            component: () => import("./views/app/oodo/production/dashboard")
          },
          {
            path: "batch-creation",
            component: () => import("./views/app/oodo/production/batch-creation")
          },
          {
            path: "batch-creation/:action/:id?",
            component: () => import("./views/app/oodo/production/batch-creation-form")
          },
          {
            path: "processing/:product",
            component: () => import("./views/app/oodo/production/processing")
          },
          {
            path: "processing/:product/:action/:id?",
            component: () => import("./views/app/oodo/production/processing-form")
          },
        ]
      },

      {
        path: "/app/purchase",
        component: () => import("./views/app/pages"),
        redirect: "/app/purchase/dashboard",
        children: [
          {
            path: "purchase-materials",
            component: () => import("./views/app/oodo/purchase/purchase-materials")
          },
          // {
          //   path: "sample-test",
          //   component: () => import("./views/app/oodo/qc/sample-test")
          // },
          // {
          //   path: "sample-test/:action/:id?",
          //   component: () => import("./views/app/oodo/qc/sample-test-form")
          // },
          
        ]
      },

      {
        path: "/app/oodo",
        component: () => import("./views/app/pages"),
        redirect: "/app/oodo/aeo",
        children: [
          {
            path: "aeo",
            component: () => import("./views/app/oodo/aeo")
          },
          {
            path: "create",
            component: () => import("./views/app/oodo/create")
          },
          {
            path: "import",
            component: () => import("./views/app/oodo/import")
          },
          {
            path: "price",
            component: () => import("./views/app/oodo/price")
          },
          {
            path: "purchase",
            component: () => import("./views/app/oodo/purchase")
          },
          {
            path: "procurement",
            component: () => import("./views/app/oodo/procurement")
          },
          {
            path: "discuss",
            component: () => import("./views/app/oodo/discuss")
          },
          {
            path: "sales",
            component: () => import("./views/app/oodo/sales")
          },
          {
            path: "fpo",
            component: () => import("./views/app/oodo/fpo")
          },
          {
            path: "ics",
            component: () => import("./views/app/oodo/employees")
          },
          {
            path: "employees",
            component: () => import("./views/app/oodo/employees")
          },
          {
            path: "apps",
            component: () => import("./views/app/oodo/apps")
          },
          {
            path: "settings",
            component: () => import("./views/app/oodo/settings")
          },
         
          
        ]
      },

    ]
  },
  // sessions
  {
    path: "/app/sessions",
    component: () => import("./views/app/sessions"),
    redirect: "/app/sessions/signIn",
    children: [
      {
        path: "signIn",
        component: () => import("./views/app/sessions/signIn")
      },
      {
        path: "signUp",
        component: () => import("./views/app/sessions/signUp")
      },
      {
        path: "forgot",
        component: () => import("./views/app/sessions/forgot")
      }
    ]
  },

  {
    path: "/vertical-sidebar",
    component: () => import("./containers/layouts/verticalSidebar")
  },
  {
    path: "*",
    component: () => import("./views/app/pages/notFound")
  }
];

const router = new Router({
  mode: "history",
  linkActiveClass: "open",
  routes,
  scrollBehavior(to, from, savedPosition) {
    return {x: 0, y: 0};
  }
});

router.beforeEach((to, from, next) => {
  // If this isn't an initial page load.
  if (to.path) {
    // Start the route progress bar.

    NProgress.start();
    NProgress.set(0.1);
  }
  next();
});


router.afterEach(() => {
  // Remove initial loading
  const gullPreLoading = document.getElementById("loading_wrap");
  if (gullPreLoading) {
    gullPreLoading.style.display = "none";
  }
  // Complete the animation of the route progress bar.
  setTimeout(() => NProgress.done(), 500);
  // NProgress.done();
  // if (isMobile) {
  if (window.innerWidth <= 1200) {
    // console.log("mobile");
    store.dispatch("changeSidebarProperties");
    if (store.getters.getSideBarToggleProperties.isSecondarySideNavOpen) {
      store.dispatch("changeSecondarySidebarProperties");
    }

    if (store.getters.getCompactSideBarToggleProperties.isSideNavOpen) {
      store.dispatch("changeCompactSidebarProperties");
    }
  } else {
    if (store.getters.getSideBarToggleProperties.isSecondarySideNavOpen) {
      store.dispatch("changeSecondarySidebarProperties");
    }

    // store.state.sidebarToggleProperties.isSecondarySideNavOpen = false;
  }
});

export default router;
